import Header from "./Header";
import Footer from "./Footer";
import React from "react";
import Items from "./Items";
import Categories from "./Categories";
class Base extends React.Component{
    constructor(props) {
        super(props);
        this.state={
            currentItems: [],
            items: [
                {
                    id: 0,
                    title: 'LabWork_1',
                    desc: 'Second lab',
                    category: 'Chemistry',
                    price: '228'
                },
                {
                    id: 1,
                    title: 'LabWork_1',
                    desc: 'First lab',
                    category: 'Math',
                    price: '50'
                },
                {
                    id: 2,
                    title: 'LabWork_2',
                    desc: '3 lab',
                    category: 'Physics',
                    price: '523'
                },
                {
                    id: 3,
                    title: 'LabWork_1',
                    desc: '4 lab',
                    category: 'Electronic',
                    price: '10000'
                },
                {
                    id: 4,
                    title: 'LabWork_2',
                    desc: 'Second lab',
                    category: 'Math',
                    price: '165'
                },
                {
                    id: 5,
                    title: 'LabWork_5',
                    desc: 'Second lab',
                    category: 'Math',
                    price: '165'
                },
                {
                    id: 6,
                    title: 'LabWork_3',
                    desc: 'Second lab',
                    category: 'Chemistry',
                    price: '228'
                },
                
            ]
        }
        this.state.currentItems = this.state.items
        this.chooseCategory = this.chooseCategory.bind(this)
    }

    chooseCategory(category){
        if(category === "")
        {
            this.setState({currentItems: this.state.items})
            return
        }
        this.setState({
            currentItems: this.state.items.filter(el => el.category === category)
        })
    }
}

export default Base;
